    var _parseContent = function(content, url) {

        var parser = new DOMParser(),
            doc = parser.parseFromString(
                content,
                /(Safari|Gecko\/|iPhone|iPad|iPod)/i.test(navigator.userAgent) ? 'text/xml' : 'text/html'),
            head, baseUrl, baseEl, htmlString;

        if (doc) {
            if (doc.getElementsByTagName('parsererror').length) {
                return null;
            }
            head = doc.getElementsByTagName('head')[0];
        }

        if (head) {

            // insert base element
            baseEl = doc.createElement('base');

            baseUrl = _toBaseUrl(url);

            if (baseUrl === null) {
                return null;
            }

            baseEl.setAttribute('href', baseUrl);
            baseEl = head.insertBefore(baseEl, head.firstChild);

            var domParser = new DOMParser();
            var contentDom = domParser.parseFromString(doc.documentElement.outerHTML, "application/xhtml+xml");
            htmlString = '<!DOCTYPE html>' + contentDom.documentElement.outerHTML;
            return htmlString;
        }

        return null;
    };

    var _toBaseUrl = function(url) {

        try {
            if (/^(http|https|file|app):\/\//i.test(url)) {
                var regex = /^app:\/\/(?:readmoo)\/(?:api)\/(?:book)/i;
                if(regex.test(url))
                    return url.replace(regex,'http://localhost:' + self.location.port); // 2016/02/25 尚未確定有何影響
                else
                    return url;
            } else {
                return url[0] === '/' ? self.location.protocol + '//' + self.location.host + url :
                    self.location.href.substring(0, self.location.href.lastIndexOf('/') + 1) + url;
            }
        } catch (e) {
            console.error(e.message);
            return null;
        }
    };

