    Helpers.loadContentByUrl = function(url) {

        var _this = this,
            dtd = $.Deferred();

        $.ajax({
            url: url,
            method: 'GET',
            timeout: 10000,
            dataType: 'text',
            cache: true
        }).done(function(data) {

            var htmlString = _parseContent(data, url);

            if (htmlString === null) {
                dtd.reject();
            } else {
                dtd.resolve(htmlString);
            }

        }).fail(function() {
            dtd.reject();
        });

        return dtd;
    };

