        console.info('loadEncryptedContentByUrl:');

        // 2016/07/19 保留基準 url
        _urlHtml = url;

        var resourcefetcher = context.resourcefetcher,
            dtd = $.Deferred();

        resourcefetcher.relativeToPackageFetchFileContents(attachedData.spineItem.href, 'text',
                    function (contentDocumentText) {
                        var htmlString = _parseContent(contentDocumentText, url);
                        // console.info('htmlString: ' + htmlString);
                        //TODO for nwapp parsing
                        var parser = new window.DOMParser();
                        var domDocument = parser.parseFromString(htmlString, 'text/html');
                        context.domDocument = domDocument;
                        if(mode == 'search')
                            dtd.resolve(htmlString);
                        else
                            resolveInternalPackageResources(context, attachedData, dtd);
                    }, function(){
                        dtd.reject();
                    });
        return dtd.promise();
