define('models/bookmark_data',['cfi_module'], function (cfiModule) {
    // ReadiumSDK.Models.BookmarkData
    return function(spineItem, foundElement, packageDocument, charOffsetCfi) {

        if (spineItem) {
            /**
             * spine item idref
             * @property idref
             * @type {string}
             */
            this.idref = spineItem.idref;

            /**
             * spine item index
             * @property index
             * @type {number}
             */
            this.index = spineItem.index;
        }

        if (foundElement) {

            if (foundElement.cfi && packageDocument && spineItem) {
                /**
                 * cfi of the first visible element
                 * @property contentCFI
                 * @type {string}
                 */
                this.contentCFI = (function () {

                    var contentCFI = foundElement.cfi,
                        packageDocumentCFI;

                    if (contentCFI.indexOf('!') === -1) {
                        packageDocumentCFI =
                            cfiModule.generatePackageDocumentCFIComponent(
                                    spineItem.idref, packageDocument);
                        return packageDocumentCFI + contentCFI;
                    }
                    else {
                        return contentCFI;
                    }
                })();
                 var packageDocumentCfi = (function(){
                    var packageDocumentCFI;
                        packageDocumentCFI =
                            cfiModule.generatePackageDocumentCFIComponent(
                                    spineItem.idref, packageDocument);
                        return packageDocumentCFI
                })();

                this.charOffsetCfi = packageDocumentCfi + charOffsetCfi;
            }

            if (foundElement.$element) {
                var element = foundElement.$element[0];
                console.log(element.nodeName);
                if (element.nodeName.toUpperCase() !== 'IMG') {
                    /**
                     * excerpt of the first visible element
                     * @property excerpt
                     * @type {string}
                     */
                    this.excerpt = (function () {

                        var textContent = element.textContent,
                            percent = foundElement.percentY > 100 ?
                                100 : foundElement.percentY,
                            index = textContent.length * (percent / 100),
                            length = 50;

                        if (index === textContent.length ||
                            (textContent.length - index) < length) {
                            return textContent.substr(textContent.length - length);
                        }
                        else {
                            return textContent.substr(index, length);
                        }
                    })();
                } else {
                    console.log(this.contentCFI);
                    this.charOffsetCfi = this.contentCFI;
                }
            }
        }
    };
});

