        scaleToWidth: function(width){
            console.debug('one_page_view.js -> scaleToWidth');
            //kyle
            this.updateMetaSize();
            var scale = width / this.meta_size.width;
            this.transformContentImmediate(scale, 0, 0);
        },

        getContentDocHeight: function(){
            // 只有捲動模式才會來此
            if(!this.$iframe || !this.$iframe.length) {
                return 0;
            }

            if (helpers.isIframeAlive(this.$iframe[0]))
            {
                var win = this.$iframe[0].contentWindow;
                var doc = this.$iframe[0].contentDocument;

                try{
                   // console.log(doc);
                   var $cover = doc.getElementById('cover');
                   // var imgHeight = $cover.getElementsByTagName('img')[0].offsetHeight;
                   // // $cover.setAttribute('style','height:auto');
                   // if(imgHeight==0){
                   //     console.log("if(imgHeight==0)");
                   //     var height = document.documentElement.offsetHeight;
                   // }else{
                   //     console.log("height = imgHeight");
                   //     var height = imgHeight;
                   // }
                   if ($cover) {
                       // 為何需要特別處理 "cover"？因為 <img> 在 <div> 內會固定有個下方留白(bug?)，影響高度。
                       if ($cover.nextElementSibling) {
                           // 2016/05/27 《大數據：教育篇》每個章節都有個 <div id="cover">，然後是內文，所以留白的高度不需省略
                           height = Math.round(parseFloat(win.getComputedStyle(doc.documentElement, null).height));
                       }
                       else {
                           // 2016/05/27 捲動模式為了美觀，書封特地只顯示圖片高度，省略留白。
                           height = $cover.getElementsByTagName('img')[0].offsetHeight;
                       }
                   }
                   else if (doc.documentElement) {
                       height = Math.round(parseFloat(win.getComputedStyle(doc.documentElement, null).height)); //body can be shorter!
                   }
                   else {
                       height = document.documentElement.offsetHeight;
                   }
               }catch(e){
                    console.log("catch(e)");
                    var height = Math.round(parseFloat(win.getComputedStyle(doc.documentElement).height)); //body can be shorter!
               }
                return height;
            }
            else if (this.$epubHtml)
            {
                console.error("getContentDocHeight ??");

                var jqueryHeight = this.$epubHtml.height();
                return jqueryHeight;
            }

            return 0;
            console.info('one_page_view.js -> getContentDocHeight');
        },

        transformContentImmediate: function(scale, left, top, method){
            console.debug('one_page_view.js -> transformContentImmediate: ' + method);
            var pageTransition_Translate = false; // TODO: from options
            var that = this;
            var pageSwitchActuallyChanged = this.pageSwitchActuallyChanged || this.pageSwitchActuallyChanged_IFRAME_LOAD;
            this.pageSwitchActuallyChanged_IFRAME_LOAD = false;
            // console.error("transformContent: "+pageSwitchActuallyChanged + " - " + _pageSwitchDir);
            console.info(this.pageSwitchActuallyChanged);
            var elWidth = Math.ceil(that.meta_size.width * scale);
            var elHeight = Math.floor(that.meta_size.height * scale);

            _.each(['-webkit-', '-moz-', '-ms-', ''], function(prefix) {
                that.$el.css(prefix + "transition", "all 0 ease 0");
            });

            if (pageSwitchActuallyChanged && this.enablePageTransitions)
            {
                if (this.pageSwitchDir === 0)
                {
                    this.$el.css("opacity", "0");
                }
                else
                {
                    if (pageTransition_Translate)
                    {
                        var initialLeft = elWidth * 0.7 * (this.pageSwitchDir === 2 ? 1 : -1);
                        var move = this.generateTransformCSS(1, Math.round(initialLeft), 0);
                        this.$el.css(move);
                    }
                    else
                    {
                        this.$el.css("opacity", "0");
                    }
                }
            }

            this.$el.css("top", "0px");
            this.$el.css("width", elWidth + "px");
            this.$el.css("height", elHeight + "px");
            this.$el.css("left", left + "px");
            this.$el.css("top", top + "px");

            // if (this.classes == 'fixed-page-frame-right' && method !== 'setCustom'){
            //     this.$el.css("left", "-1px");
            //     this.$el.css("display", "inline-block");
            //     this.$el.css("position", "relative");
            // }
            // else if (this.classes == 'fixed-page-frame-right' && method == 'setCustom') {
            //     this.$el.css("left", this.$el.css("width"));
            //     this.$el.css("display", "");
            //     this.$el.css("position", "absolute");
            // }
            // else if (this.classes == 'content-doc-frame' && method !== 'setCustom') {
            //     this.$el.css("left", left + "px");
            //     this.$el.css("top", top + "px");
            //     this.$el.css("width", elWidth + "px");
            //     this.$el.css("height", elHeight + "px");
            //     // this.$el.css("width", elWidth + "px");
            //     // this.$el.css("height", elHeight + "px");

            // }
            // else { //TODO content width exceed screen width
            //     this.$el.css("left", "0px");
            //     this.$el.css("display", "inline-block");
            //     this.$el.css("position", "relative");
            // }

            // this.$iframe.css("width", elWidth + "px");
            // this.$iframe.css("height", elHeight + "px");

            // var css = this.generateTransformCSS(scale, 0, 0);

            var css = helpers.CSSTransformString({scale : scale});

            css["width"] = this.meta_size.width;
            css["height"] = this.meta_size.height;

            this.$scaler.css(css);

            if(!this.$epubHtml) {
                // debugger;
                return;
            }

            /*var css = ReadiumSDK.Helpers.CSSTransformString({scale : scale});
            css["width"] = _meta_size.width;
            css["height"] = _meta_size.height;
            _$scaler.css(css);*/

            // Chrome workaround: otherwise text is sometimes invisible (probably a rendering glitch due to the 3D transform graphics backend?)
            //_$epubHtml.css("visibility", "hidden"); // "flashing" in two-page spread mode is annoying :(
            this.$epubHtml.css("opacity", "0.999");

            this.showIFrame();

            setTimeout(function()
            {
                //_$epubHtml.css("visibility", "visible");
                that.$epubHtml.css("opacity", "1");
            }, 0);

            if (pageSwitchActuallyChanged && this.enablePageTransitions)
            {
                setTimeout(function()
                {
                    if (this.pageSwitchDir === 0)
                    {
                        _.each(['-webkit-', '-moz-', '-ms-', ''], function(prefix) {
                            that.$el.css(prefix + "transition", "opacity 250ms linear");
                        });

                        this.$el.css("opacity", "1");
                    }
                    else
                    {
                        if (pageTransition_Translate)
                        {
                            this.$el.css("-webkit-transition", "-webkit-transform 200ms ease-out");
                            var css = {};
                            _.each(['-webkit-', '-moz-', '-ms-', ''], function(prefix) {
                                //css[prefix + 'transition'] = prefix + "transform 200ms ease-out";
                                css[prefix + 'transform'] = "none";
                            });
                            this.$el.css(css);
                        }
                        else
                        {
                            _.each(['-webkit-', '-moz-', '-ms-', ''], function(prefix) {
                                that.$el.css(prefix + "transition", "opacity 150ms ease-out");
                            });

                            this.$el.css("opacity", "1");
                        }
                    }

                    // var moveBack = generateTransformCSS(0, 0, 0);
                    // _$el.css(css);
                    //
                //_$el.css("left", left + "px");

                }, 10);
            }
        },

