            AT_LAST_PAGE: 'atlastpage',
            SPINE_ITEM_CHANGED: 'spineitemchanged',
            BEFORE_SPINE_ITEM_CHANGED: 'beforespineitemchanged',
            SPINE_ITEM_NOT_FOUND: 'spineitemnotfound',
            NEXT_PAGE: 'nextpage',
            PREVIOUS_PAGE: 'previouspage',
            RENDER_COMPLETED: 'rendercompleted',
            CONTENT_DOCUMENT_LOAD_START: 'contentdocumentloaded',
            MEDIA_OVERLAY_STATUS_CHANGED: 'mediaoverlaystatuschanged',
