        updateLoadedViewsTop: function(callback, assertScrollPosition) {
            var that = this;
            // console.info('scroll_view.js -> updateLoadedViewsTop');
            if(this.stopTransientViewUpdate) {
                // console.info('case1');
                callback();
                return;
            }

            var viewPage = this.firstLoadedView();
            if(!viewPage) {
                // console.info('case2');
                callback();
                return;
            }

            var viewPortRange = this.getVisibleRange(0);
            var firstViewRange = this.getPageViewRange(viewPage);
            var that = this;

            // console.warn('viewPortRange.top: ' + viewPortRange.top + 'firstViewRange.top: ' + firstViewRange.top);
            // console.warn('viewPortRange.top: ' + viewPortRange.top+ 'firstViewRange.bottom: ' + firstViewRange.bottom);

            if((viewPortRange.top - firstViewRange.bottom) > this.ITEM_LOAD_SCROLL_BUFFER) {
                // console.info('case3');
                var scrollPos = this.scrollTop();
                this.removePageView(viewPage);
                this.scrollTo(scrollPos - (firstViewRange.bottom - firstViewRange.top), undefined);
                this.assertScrollPosition(that,"updateLoadedViewsTop 1");
                this.updateLoadedViewsTop(callback, assertScrollPosition); //recursion
            }
            else if((viewPortRange.top - firstViewRange.top) < this.ITEM_LOAD_SCROLL_BUFFER) {
                // console.info('case4');
                var prevSpineItem = this.spine.prevItem(viewPage.currentSpineItem(), true);
                if(!prevSpineItem){
                    // console.info('case4-1');
                    callback();
                } else {
                    this.addToTopOf(viewPage, function(isElementAdded){
                        if(isElementAdded) {
                            that.assertScrollPosition(that,"updateLoadedViewsTop 2");
                            that.updateLoadedViewsTop(callback, assertScrollPosition); //recursion
                        }
                        else {
                            callback();
                        }
                    });
                }

            }
            else {
                // console.info('case5');
                callback();
            }

        },

        updateLoadedViewsBottom: function(callback, assertScrollPosition) {
            // console.debug('scroll_view.js -> updateLoadedViewsBottom callback: ');
            // console.debug(callback);
            var that = this;
            if(this.stopTransientViewUpdate) {
                 // console.debug('_stopTransientViewUpdate');
                callback(that);
                return;
            }

            var viewPage = this.lastLoadedView();
            if(!viewPage) {
                // console.debug('!viewPage');
                callback(that);
                return;
            }

            var viewPortRange = this.getVisibleRange(0);
            var lastViewRange = this.getPageViewRange(viewPage);
            var that = this;
            // console.warn('viewPortRange.bottom: ' + viewPortRange.bottom + 'lastViewRange.top: ' + lastViewRange.top);
            // console.warn('viewPortRange.bottom: ' + viewPortRange.bottom + 'lastViewRange.bottom: ' + lastViewRange.bottom);

            if((lastViewRange.top - viewPortRange.bottom) > this.ITEM_LOAD_SCROLL_BUFFER) {
                // console.debug('0805 case1');
                this.removePageView(viewPage);
                this.assertScrollPosition(that,"updateLoadedViewsBottom 1");
                this.updateLoadedViewsBottom(callback, assertScrollPosition); //recursion
            }
            else if((lastViewRange.bottom - viewPortRange.bottom) < this.ITEM_LOAD_SCROLL_BUFFER) {
                  // console.debug('0805 case2');
                this.addToBottomOf(viewPage, function(newPageLoaded) {
                    that.assertScrollPosition(that,"updateLoadedViewsBottom 2");
                    if(newPageLoaded) {
                        that.updateLoadedViewsBottom(callback, assertScrollPosition); //recursion
                    }
                    else {
                        callback(that);
                    }
                });
            }
            else {
                callback(that);
            }

        },


        // This function double-checks whether the browser has shifted the scroll position because of unforeseen rendering issues.
        // (this should never happen because we handle scroll adjustments during iframe height resizes explicitely in this code)
        assertScrollPosition: function(self,msg){
            console.debug('scroll_view.js -> assertScrollPosition');
            if (self.DEBUG)
            {
                if (!self.scrollPosBefore) return;
                var scrollPosAfter = undefined;

                var offset = self.MytmpView.offset();
                if (offset) scrollPosAfter = offset.top;

                if (!scrollPosAfter) return;
                var diff = scrollPosAfter - self.scrollPosBefore;
                if (Math.abs(diff) > 1)
                {
                    console.debug("@@@@@@@@@@@@@@@ SCROLL ADJUST (" + msg + ") " + diff + " -- " + pageView.currentSpineItem().href);
                    //this.$contentFrame[0].scrollTop = this.$contentFrame[0].scrollTop + diff;
                }
            }
        },

