        findPageViewForSpineItem: function(spineItem, reverse) {

            var retView = undefined;

            this.forEachItemView(function(pageView) {
                if(pageView.currentSpineItem() == spineItem) {
                    retView = pageView;
                    //brake the iteration
                    return false;
                }
                else {
                    return true;
                }

            }, reverse);

            return retView;
        },

        forEachItemView: function(func, reverse) {

            var pageNodes = this.$contentFrame.children();
            // console.debug('pageNodes.length ' + pageNodes.length);

            var count = pageNodes.length;
            var iter = reverse ? function(ix) { return ix - 1}
                               : function(ix) { return ix + 1};

            var compare = reverse ? function(ix) { return ix >= 0}
                                  : function(ix) { return ix < count };

            var start = reverse ? count - 1 : 0;

            for(var i = start; compare(i); i = iter(i)) {

                var $element = pageNodes.eq(i);
                var curView = $element.data("pageView");
                // console.info('0201 fetch current PageView: ' + curView.getContentDocHeight());

                if(curView) {
                    if(func(curView) === false) {
                        return;
                    }
                }
            }
        },

        getCurrentScrollView: function(){

            var pageNodes = this.$contentFrame.children(),
                count = pageNodes.length,
                // 在 scrollview 內的任意 y 座標
                remainHeight = this.hole_scrollHeight,
                adjust_height = 0;

            for(var i = 0; i < count; i++){
                var $element = pageNodes.eq(i),
                    curView = $element.data("pageView"),
                    height;
                if(!curView)
                    return null;

                // 來自 2015/02/02：對於流式的計算正確，那時尚無版式書籍
                // if (typeof(curView.epubHtml) !== 'undefined')
                //     height = window.getComputedStyle(curView.epubHtml).height;
                // 2016/06/15 版式的 scaler 有 transform: scale(1.80937); 去延伸 viewport，會導致用 curView.epubHtml 的高度判斷失真
                if ($element) {
                    height = window.getComputedStyle($element[0]).height;
                }
                else
                    height = "0px"; // 2016/06/13 讓此函數可適用更多狀況
                    // return null;

                // 調整到相對於所在 iframe 的 Y 軸位置
                adjust_height = remainHeight;
                // console.info('adjust height: ' + adjust_height);
                // 減去前面每一個 iframe 的高度
                remainHeight -= parseInt(height.split('px')[0])
                if(remainHeight <= 0){
                    console.log("getCurrentScrollView() return index = ", i);
                    return {
                        num: i, //iframe index
                        height: adjust_height
                    }
                }
            }

        },

