        loadSpineItem: function(spineItem, callback) {
            console.info('scroll_view.js => loadSpineItem');
            this.removeLoadedItems();

            var that = this;
            var scrollPos = this.scrollTop();
            console.debug('createPageViewForSpineItem@loadSpineItem');
            var loadedView = this.createPageViewForSpineItem();
            // this.trigger(this.EVENT.SPINE_ITEM_NOT_FOUND);

            this.$contentFrame.append(loadedView.element());

            loadedView.loadSpineItem(spineItem, function(success, $iframe, spineItem, isNewlyLoaded, context) {

                if(success) {
                    console.info('scroll_view.js -> loadSpineItem \" success case \"');
                    // console.debug('loadedView.element(): ' + loadedView.element);
                    console.info('resolve loadedView!!');

                    var continueCallback = function(successFlag)
                    {
                        console.debug(' >>>>>>>>> continueCallback@loadSpineItem >>>>>>');
                        that.onPageViewLoaded(loadedView, success, $iframe, spineItem, isNewlyLoaded, context);

                        callback(loadedView);

                        //successFlag should always be true as loadedView iFrame cannot be dead at this stage.
                    };

                    that.reachStableContentHeight(1, loadedView, $iframe[0], spineItem.href, spineItem.isFixedLayout(), spineItem.isFixedLayout() ? loadedView.meta_width() : 0, "openPage", continueCallback); // //onIFrameLoad called before this callback, so okay.
                    _.defer(function() {
                        that.trigger(that.EVENT.RENDER_COMPLETED);
                    });
                }
                else {
                    console.error("Unable to load " + spineItem.href);

                    that.removePageView(loadedView);
                    loadedView = undefined;
                }

                callback(loadedView);

            }, that);

        },

