            var viewportHeight = this.$viewport.height();
            console.log('viewportWidth: ' + viewportWidth);

            var leftPageMargins = this.leftPageView.isDisplaying() ? helpers.Margins.fromElement(this.leftPageView.element()) : helpers.Margins.empty();
