define('models/renderer_spine',[
    'backbone',
    './renderer_spine_item',
    '../helpers'
], function (Backbone, SpineItem, helpers) {
    // ReadiumSDK.Models.Spine
    return Backbone.Model.extend({

        /*
         * Collection of spine items
         * @property items
         * @type {Array}
         */
        items: [],

        /*
         * Page progression direction ltr|rtl|default
         * @property direction
         * @type {string}
         */
        direction: undefined,

        SPREAD_LEFT: "page-spread-left",
        SPREAD_RIGHT: "page-spread-right",

        /*
         * @property package
         * @type {Package}
         *
         */
        package: undefined,

        initialize : function() {

            console.info('new a Spine Object!');

            this.reset();

            this.package = this.get("package");
            var spineData = this.get("spineData");

            if(spineData) {

                this.direction = spineData.direction;
                if(!this.direction) {
                    this.direction = "ltr";
                }

                var length = spineData.items.length;
                for(var i = 0; i < length; i++) {
                    var item = new SpineItem(spineData.items[i], i, this);
                    this.items.push(item);
                }
                this.updateSpineItemsSpread();
            }

        },

        reset: function() {
            this.items = [];
            this.direction = undefined;
            this.package = undefined;
        },

        prevItem:  function(item) {

            if(this.isValidIndex(item.index - 1)) {
                return this.items[item.index - 1];
            }

            return undefined;
        },

        nextItem: function(item){

            if(this.isValidIndex(item.index + 1)) {
                return this.items[item.index + 1];
            }

            return undefined;
        },

        getItemUrl: function(item) {

            if(this.package.rootUrl) {

                if(helpers.EndsWith(this.package.rootUrl, "/")){
                    return this.package.rootUrl + item.href;
                }
                else {
                    return this.package.rootUrl + "/" + item.href;
                }
            }

            return item.href;

        },

        isValidIndex: function(index) {

            return index >= 0 && index < this.items.length;
        },

        first: function() {
            return this.items[0];
        },

        last: function() {
            return this.items[this.items.length - 1];
        },

        item: function(index) {
            return this.items[index];
        },

        isRightToLeft: function() {

            return this.direction === "rtl";
        },

        isLeftToRight: function() {

            return !this.isRightToLeft();
        },

        getItemById: function(idref) {

            var length = this.items.length;

            for(var i = 0; i < length; i++) {
                if(this.items[i].idref == idref) {

                    return this.items[i];
                }
            }

            return undefined;
        },

        getItemByHref: function(href) {

            var length = this.items.length,
                item, pos;
            for(var i = 0; i < length; i++) {
                item = this.items[i];
                pos = href.lastIndexOf(item.href);
                if (pos == 0 || href[pos - 1] == '/') {
                    return item;
                }
            }

            return undefined;
        },

        updateSpineItemsSpread: function(){

            var len = this.items.length;
            var isFirstPageInSpread = false;
            var baseSide = this.isLeftToRight() ? this.SPREAD_LEFT : this.SPREAD_RIGHT;

            for(var i = 0; i < len; i++) {

                var spineItem = this.items[i];
                if( !spineItem.page_spread) {

                    var spread = isFirstPageInSpread ? baseSide : spineItem.alternateSpread(baseSide);
                    spineItem.setSpread(spread);
                }

                isFirstPageInSpread = !spineItem.isRenditionSpreadAllowed() || spineItem.page_spread != baseSide;
            }
        }

    });
});

