define('models/package',[
    'backbone',
    './renderer_spine',
    './media_overlay',
    '../helpers'
], function (Backbone, Spine, MediaOverlay, helpers) {
    // ReadiumSDK.Models.Package
    return Backbone.Model.extend({

        spine: undefined,
        rendition_layout: undefined,
        rendition_flow: undefined,
        redition_spread: undefined,
        rendition_orientation: undefined,

        rootUrl: undefined,
        rootUrlMO: undefined,
        media_overlay: undefined,

        initialize : function() {

            this.reset();

            var packageData = this.get("packageData");

            if(packageData) {

                this.rootUrl = packageData.rootUrl;
                this.rendition_layout = packageData.rendition_layout;

                if(!this.rendition_layout) {
                    this.rendition_layout = "reflowable";
                }

                this.spine = new Spine({spineData: packageData.spine, package: this});
                console.debug("check packageData.media_overlay: " + JSON.stringify(packageData.media_overlay));
                this.media_overlay = MediaOverlay.fromDTO(packageData.media_overlay, this);
                
            } else {
                console('without any packageData!');
            }
        },

        resolveRelativeUrlMO: function(relativeUrl) {

            if(this.rootUrlMO && this.rootUrlMO.length > 0) {
                if(Helpers.EndsWith(this.rootUrlMO, "/")){
                    return this.rootUrlMO + relativeUrl;
                }
                else {
                    return this.rootUrlMO + "/" + relativeUrl;
                }
            }
            return this.resolveRelativeUrl(relativeUrl);
        },

        resolveRelativeUrl: function(relativeUrl) {

            if(this.rootUrl) {

                if(Helpers.EndsWith(this.rootUrl, "/")){
                    return this.rootUrl + relativeUrl;
                }
                else {
                    return this.rootUrl + "/" + relativeUrl;
                }
            }

            return relativeUrl;
        },

        reset: function() {
            this.spine = undefined;
            this.rendition_layout = undefined;
            this.rootUrl = undefined;
        },

        isFixedLayout: function() {
            return this.rendition_layout === "pre-paginated";
        },

        isReflowable: function() {
            return !this.isFixedLayout();
        },

        resolveRelativeUrl: function(relativeUrl) {

            if(this.rootUrl) {

              if(helpers.EndsWith(this.rootUrl, "/")){
                  return this.rootUrl + relativeUrl;
              }
              else {
                  return this.rootUrl + "/" + relativeUrl;
              }
          }

          return relativeUrl;
        }
    });
});

