        /**
         * Triggers the process of opening the book and requesting resources specified in the packageData
         *
         * @method openBook
         * @param {Object} openBookData object with open book data in format:
         * {
         *     package: packageData, (required)
         *     openPageRequest: openPageRequestData, (optional) data related to open page request
         *     settings: readerSettings, (optional)
         *     styles: cssStyles (optional)
         * }
         *
         */
        openBook: function(openBookData) {

            console.info('reader_view.js ->openBook');
            localStorage.setItem('_online_', false); //Due to OAuth plugin loaded after this function call, so set state to "false"

            this.package = new Package({
                packageData: openBookData.package // generate by EpubModule
            });
            this.package.document = openBookData.packageDocument;
            this.spine = this.package.spine;
            this.epub = openBookData.epub;

            if(this._mediaOverlayPlayer){
                this._mediaOverlayPlayer.reset();
            }

            var ua = navigator.userAgent;
            if (ua.indexOf('X11; ; U; Linux armv7l;') == -1) { //kindle paperwhite ua: Mozilla/5.0 (X11; ; U; Linux armv7l; en-us) AppleWebKit/534.26+ (KHTML, like Gecko) Version/5.0 Safari/534.26+
                this._mediaOverlayPlayer = new MediaOverlayPlayer(this, $.proxy(this.onMediaPlayerStatusChanged, this));
                this._mediaOverlayPlayer.setAutomaticNextSmil(this.viewerSettings.get('mediaOverlaysAutomaticPageTurn') ? true : false);
                this._mediaOverlayDataInjector = new MediaOverlayDataInjector(this, this.package.media_overlay, this._mediaOverlayPlayer);
            }

            // console.debug('after ini tiate this._mediaOverlayDataInjector: ' + this._mediaOverlayDataInjector);

            this.resetCurrentView();

            if (openBookData.settings) {
                this.updateSettings(openBookData.settings);
            }

            if(openBookData.fetcher){
                this.resourcefetcher = openBookData.fetcher;
                // console.info('fetcher: ' + this.resourcefetcher);
            }

            // if(openBookData.styles) {
            //     this.setStyles(openBookData.styles);
            // }

            if (openBookData.openPageRequest) {
                console.debug('reader_view --> openBookData.openPageRequest');

                var pageRequestData = openBookData.openPageRequest;

                if (pageRequestData.elementCfi) {
                    this.openSpineItemElementCfi(pageRequestData.elementCfi);
                } else if (pageRequestData.spineItemIndex) {

                    if (pageRequestData.spineItemPageIndex) {
                        this.openSpineItemPage(pageRequestData.spineItemIndex, pageRequestData.spineItemPageIndex);
                    } else if (pageRequestData.percent) {
                        this.openSpineItemPagePercent(pageRequestData.spineItemIndex, pageRequestData.percent);
                    } else {
                        this.openSpineItemPage(pageRequestData.spineItemIndex, 0);
                    }
                }
                // Not supported
                // else if(pageRequestData.contentRefUrl && pageRequestData.sourceFileHref) {
                //     this.openContentUrl(pageRequestData.contentRefUrl, pageRequestData.sourceFileHref);
                // }
                else {
                    // console.log("Invalid page request data: idref required!");
                    console.warn("Invalid page request data");
                }
            } else { // if we where not asked to open specific page we will open the first one
                console.debug('reader_view --> openPageRequest');
                var spineItem = this.spine.first();
                if (spineItem) {
                    var pageOpenRequest = new PageOpenRequest(spineItem, this);
                    pageOpenRequest.setFirstPage();
                    console.debug('pageOpenRequest: ' + pageOpenRequest);
                    this.openPage(pageOpenRequest);
                }
            }
        },

