        /**
         * Opens the content document specified by the url
         *
         * @method openContentUrl
         *
         * @param {string} contentRefUrl Url of the content document
         * @param {string | undefined} sourceFileHref Url to the file that contentRefUrl is relative to. If contentRefUrl is
         * relative ot the source file that contains it instead of the package file (ex. TOC file) We have to know the
         * sourceFileHref to resolve contentUrl relative to the package file.
         *
         */
        openContentUrl: function(contentRefUrl, sourceFileHref, initiator) {
            console.info('reader_view.js -> openContentUrl');

            var combinedPath = helpers.ResolveContentRef(contentRefUrl, sourceFileHref);
            var hashIndex = combinedPath.indexOf("#");
            var hrefPart;
            var elementId;
            var self = this;
            if(hashIndex >= 0) {
                hrefPart = combinedPath.substr(0, hashIndex);
                elementId = combinedPath.substr(hashIndex + 1);
            }
            else {
                hrefPart = combinedPath;
                elementId = undefined;
            }

            var spineItem = this.spine.getItemByHref(hrefPart);
            if(!spineItem) {
                console.warn('spineItem ' + hrefPart + ' not found');
                // sometimes that happens because spine item's URI gets encoded,
                // yet it's compared with raw strings by `getItemByHref()` -
                // so we try to search with decoded link as well
                var decodedHrefPart = decodeURIComponent(hrefPart);
                spineItem = this.spine.getItemByHref(decodedHrefPart);
                if (!spineItem) {
                    console.warn('decoded spineItem ' + decodedHrefPart + ' missing as well');
                    return false;
                }
            }

            return self.openSpineItemElementId(spineItem.idref, elementId, initiator);
        },

        /**
         *
         * Returns the bookmark associated with currently opened page.
         *
         * @method bookmarkCurrentPage
         *
         * @return {string} Stringified ReadiumSDK.Models.BookmarkData object.
         */
        bookmarkCurrentPage: function() {
            var x = 380, y = 290;
            var caretPosition = this.getCaretPositionFromPoint(x,y);
            var result = undefined;
            var viewType = this.getCurrentViewType();
            if (caretPosition && viewType != this.TYPE.VIEW_TYPE_SCROLLED_CONTINUOUS) {
              var eNode = caretPosition.offsetNode; //todo element Node
              var offset = caretPosition.offset;
              var startOffset, endOffset;
              if (offset > 1)
                startOffset = offset -2
              else if (offset > 0)
                startOffset = offset -1;
              else
                startOffset = offset;

              if (startOffset == offset)
                endOffset = offset + 1;
              else
                endOffset = offset;

              // console.log(caretPosition);
              // console.log(eNode);
              // console.log(startOffset);
              // console.log(endOffset);

              if (eNode.nodeType == Node.TEXT_NODE){
                // var result = cfiModule.generateCharOffsetRangeComponent(eNode, startOffset, eNode, endOffset);
                result = cfiModule.generateCharacterOffsetCFIComponent(eNode, startOffset);
              } else {
                result = cfiModule.generateElementCFIComponent(eNode, startOffset);
              }
              // console.log(result);
            }

            return this.currentView.bookmarkCurrentPage(result);
        },

