        getCurrentViewOffsetXY: function() {
            // console.debug('--- call getCurrentViewOffsetXY! ---\n\n');

            // 不用事先判斷offset是否存在，
            // 會導致視窗resize之後無法劃線。
            // var offset = this.offset;
            // if (!offset) {
            // }

            var iframe,
                iframeComputedStyle,
                paddingLeft, marginLeft, borderLeftWidth,
                paddingTop, marginTop, borderTopWidth,
                offsetTop, offsetLeft;

            var offset = this.offset;
            var ele = document.getElementById('mooreader-reader');
            var mooreaderReaderPL = parseInt(window.getComputedStyle(ele, null).getPropertyValue('padding-left').split('p')[0]);
            var readerContainerML = parseInt(window.getComputedStyle($('#epub-reader-container')[0],null).getPropertyValue("margin-left").split('p')[0]);
            if (this.currentView.iframe) {
                iframe = this.currentView.iframe;
            } else if (this.desiredViewType == this.TYPE.VIEW_TYPE_FIXED) {
                var views = this.currentView.getDisplayingViews();
                if (views.length == 0)
                    return offset;
                else
                    iframe = views[0].iframe;
            } else if (this.desiredViewType == this.TYPE.VIEW_TYPE_SCROLLED_CONTINUOUS) {
                // iframe = this.currentView.getFirstVisiblePageView().iframe;
                // if (this.viewerSettings.get('isSyntheticSpread')){
                //     offset = {
                //         x: 75,
                //         y: 50
                //     };
                // } else {
                    
                    // console.log('ele', ele.offsetTop)
                    offset = {
                        x: mooreaderReaderPL + readerContainerML,
                        y: ele.offsetTop
                    };
                // }
                return offset;
            }

            // console.debug('Content of iframe:' + iframe);
            if(!iframe){
                console.info('skipping due to weird iframe');
                return offset;
            }

            // Firefox 需要強迫設定 getComputedStyle 第二個參數
            iframeComputedStyle = window.getComputedStyle(iframe, null);

            paddingLeft = parseFloat(iframeComputedStyle.paddingLeft);
            marginLeft = parseFloat(iframeComputedStyle.marginLeft);
            borderLeftWidth = parseFloat(iframeComputedStyle.borderLeftWidth);

            paddingTop = parseFloat(iframeComputedStyle.paddingTop);
            marginTop = parseFloat(iframeComputedStyle.marginTop);
            borderTopWidth = parseFloat(iframeComputedStyle.borderTopWidth);

            offsetTop = helpers.getIframeOffset(iframe, 'offsetTop', 0);
            offsetLeft = helpers.getIframeOffset(iframe, 'offsetLeft', 0);

            offset = {
                x: offsetLeft + paddingLeft + marginLeft + borderLeftWidth,
                y: offsetTop + paddingTop + marginTop + borderTopWidth
            };

            this.offset = offset;

            return offset;
        },

        /**
         * Opens the page of the spine item with element with provided cfi
         *
         * @method openSpineItemIndexElementCfi
         *
         * @param {string} idref Id of the spine item
         * @param {string} elementCfi CFI of the element to be shown
         */
        openSpineItemElementCfi: function(elementCfi, classBlacklist, elementBlacklist, idBlacklist) {

            /*console.info('openSpineItemElementCfi => elementCfi: ' + elementCfi + '\nclassBlacklist: ' + classBlacklist +
            'elementBlacklist: ' + elementBlacklist + '\nidBlacklist: ' + idBlacklist);*/

            var spineItem, pageData,
                matcher, spineItemIndex,
                dtd = $.Deferred();
                CFI_REGEX = /^(?:(\/\d+)+\!)(?:(?:\/(?=\d+)[^\/]*)+)$/;

            if (elementCfi){

                matcher = CFI_REGEX.exec(elementCfi);

                if (matcher) {
                    spineItemIndex = (parseInt(matcher[1].substr(1)) / 2) - 1;
                    if (spineItemIndex % 1 !== 0 || spineItemIndex < 0) {
                        console.warn("It is not a valid CFI string \"" + cfi + "\"");
                        return;
                    }
                }

                if (isNaN(spineItemIndex)) {
                    console.warn("A \"spineItemIndex\" must be provided");
                    return;
                }
            }

            if(!classBlacklist)
                spineItem = this.spine.item(spineItemIndex);
            else
                spineItem = this.getSpineItem(classBlacklist); //classBlacklist is eq idref in this case

            if (!spineItem) {
                return;
            }

            pageData = new PageOpenRequest(spineItem, this);

            // 2016/05/23 修正此函數所需的參數。為何 2014/12/19 要改變這裡？當時有註記：Bug fix: jump to cfi function in search result failed.
            pageData.setElementCfi(
                elementCfi, classBlacklist, elementBlacklist, idBlacklist);

            // pageData.setElementCfi(elementCfi);
            this.openPage(pageData, dtd);
            return dtd.promise();
        },

