        searchSpineItemIndex: function(index, text) {

            var viewType = this.getCurrentViewType();
            var that = this;
            var spineItem = this.spine.item(index),
                url,
                dtd,
                encrypted,
                mode = 'search';

            if (!spineItem) {
                return null;
            }

            url = this.spine.getItemUrl(spineItem);

            if (!url) {
                return null;
            }

            dtd = $.Deferred();
            defer = function(content) {

                    if (!content) {
                        return dtd.reject();
                    }

                    var userAgent = navigator.userAgent,
                        parser = new DOMParser(),
                        doc = parser.parseFromString(
                            content,
                            /(Safari\/|iPhone|iPad|iPod)/i.test(userAgent) ?
                            'text/xml' : 'text/html');

                    if (doc.getElementsByTagName('parsererror').length) {
                        return dtd.reject();
                    }

                    helpers.searchText(doc, text)
                        .progress(function(result) {

                            if (result === 'done') {
                                dtd.resolve();
                                return;
                            }
                            // composite packageDocumentCFI and rangeCFI
                            result.cfi = cfiModule.generatePackageDocumentCFIComponentWithSpineIndex(
                                index,
                                that.epub.getPackageDocumentXmlDom()
                            ) + result.cfi;

                            result.idref = spineItem.idref;

                            dtd.notify(result);

                            // FIXME async function may cause output not in expect sequence
                            // get spine item title
                            // that.epub.getSpineItemTitleByIndex(index, function(title) {
                            //     result.title = title;

                            //     /*
                            //      * example:
                            //      * {
                            //      *   cfi: (string),
                            //      *   title: (string),
                            //      *   suffix: (string),
                            //      *   prefix: (string),
                            //      *   text: (string)
                            //      * }
                            //      *
                            //      */
                            //     dtd.notify(result);
                            // });

                        }).fail(function() {
                            dtd.reject();
                        });
            };

            encrypted = this.currentView.resourcefetcher.shouldConstructDomProgrammatically();
            console.info('Document been encrypted: ' + encrypted);

            if(!encrypted){
                helpers.loadContentByUrl(url)
                    .done(defer)
                    .fail(function() {
                        dtd.reject();
                    });
            } else {
                helpers.loadEncryptedContentByUrl(url, {spineItem: spineItem}, this, mode)
                    .done(defer)
                    .fail(function(){
                        dtd.reject();
                    });
            }

            return dtd;
        },

