var thumbnailing = function(start) {
    // console.log('thumbnailing');
    var that = this;

    var thumbnailDtd = function(spineItemIndex, maxIndex){
        // console.log('thumbnailDtd');
        var dtd = reader.thumbnailSpineItemIndex(spineItemIndex, maxIndex);
        return dtd.promise();
    }; 

    var thumbnailOver = function() {
        var responses = APPLICATION === 'nwapp' ? global.window.promises : arguments,
            $scroll = $('.scrolls'),
            index = 0,
            buffer = [];

        Promise.all(responses).then(function(dataArr){
            dataArr.forEach(function(canvas){
               var extra_canvas = document.createElement("canvas");
               extra_canvas.setAttribute('width',150);
               extra_canvas.setAttribute('height',205);
    
               var ctx = extra_canvas.getContext('2d');
    
               ctx.drawImage(canvas, 0, 0, canvas.width, canvas.height,0,0,150,205);
               var dataURL = extra_canvas.toDataURL();
               var obj = { '_id': new Date().toJSON(), itemIndex: start, dataURL: dataURL};
               var img = $(document.createElement('img'));

               // img.css({'cursor':'pointer', 'border-width': 'thin', 'border-style': 'double', 'margin': '0px 2px', 'transition': 'all 0.5s', 'background-color': 'white'}); 
               img.attr({'src': dataURL, 'id': start, 'title': 'page'+(start+1)});
               start+=1;
               $scroll.append(img); 
               buffer.push(obj);
            });
            
            db.bulkDocs(buffer).then(function (result) {
              // handle result
              // console.log('finish insert & result: ' + result);
              $thumbLoading.removeClass('show');
            }).catch(function (err) {
              // console.log(err);
            });
            $('.scrolls img').on('click', function(event) { 
              event.stopImmediatePropagation(); 
              reader.openSpineItemPagePercent($(this).attr('id'));
              $('#navigatorbar').removeClass('show');
              $('.sub-nav').removeClass('show');
              $('#thumbnail').removeClass('active');
            });

        });
    };

    var spineItemCount = 0;
    var spineItem = reader.getSpineItemByIndex(spineItemCount);
    while (spineItem) {
        spineItemCount += 1;
        spineItem = reader.getSpineItemByIndex(spineItemCount);
    }     
    var i , l = spineItemCount, $scroll = $('.scrolls'), $thumbnail = $('.thumbnail-control');

    var appendThumbnail = function() {
        var j, promises = [];
         if ((start+10) >= spineItemCount)
             var max = spineItemCount;
         else
             var max = start+10;

        for (j = start; j < max; j++) {
            // console.log('appendThumbnail');
            var Id = '#thumbnail-' + j;
            var iframe = $thumbnail.find(Id)[0];
            var promise = html2canvas(iframe.contentDocument.body);
            promises.push(promise);
            // console.log(iframe.contentDocument.body);
        }
        // console.log(promises);
        if (APPLICATION == 'nwapp') {
            global.window.promises = promises;
        }
        $.when.apply($, promises).done(thumbnailOver);
    }; 

    if ((start+10) >= spineItemCount)
        var max = spineItemCount;
    else
        var max = start+10;

    for (i = start; i < max; i++) {      
        // console.log('max: ' + max);
        // console.log('i: ' + i)
        var dtd = thumbnailDtd(i, max);
        if(!dtd){
            // console.info('since like something wrong with the document');
        }
        dtd
        .done(appendThumbnail);
    }      
};

