$tpl.on(clickEvent, '#single-page, #synthetic-page', function (event) {
    event.stopPropagation();
    readmoo.localStorage.setItem('scrollMode', 'default');
    var target = event.target;
    var $target = $(target);
    var view_type = reader.getCurrentViewType();
    $target.addClass('active');
    $target.siblings().removeClass('active');

    if(view_type == 2)
        reader.setSyntheticSpreadFixed(target.id === 'single-page' ? 'single' : 'auto');
    else
        reader.setSyntheticSpread(target.id === 'single-page' ? false : true);

    if (target.id === 'single-page' && scrollToReflowable === false){
        useCustomLayout = true;
        readmoo.localStorage.setItem('synthetic', 'single');
        // $('#mooreader-title').css('display', 'none');
    } else if (scrollToReflowable === false) {
        useCustomLayout = false;
        readmoo.localStorage.setItem('synthetic', 'double');
        // $('#mooreader-title').css('display', 'none');
    } else {
        scrollToReflowable = false;
        var synthetic = (target.id === 'single-page' ? false : true);
        if (target.id === 'single-page')
            readmoo.localStorage.setItem('synthetic', 'single');
        else
            readmoo.localStorage.setItem('synthetic', 'double');
        readerSettings = { isScrollContinuous: false, doNotUpdateView: false, isSyntheticSpread: synthetic};
        updateReader(readerSettings);
        if(readerSettings.isSyntheticSpread)
            $tpl.find('#synthetic-page').trigger(clickEvent);
        else
            $tpl.find('#single-page').trigger(clickEvent);
    }

    lazyDefaultLayout(window.innerWidth);
    $tpl.trigger(EVENT_CHANGE_LAYOUT, [target.id]);
    return;
});

$tpl.on(clickEvent, '#scrolling-page', function (event){
    event.stopPropagation();
    var target = event.target,
        $target = $(target),
        readerSettings;

    var currentSpineItem = reader.getCurrentSpineItem();
    
    if (currentSpineItem.isFixedLayout()) {
        $('.thumbnail-control').hide();
        $('#scale').hide();
        $('.scale-control').hide();
        $('#mooreader-title').css('display', 'block');
    }
        
    $target.addClass('active');
    $target.siblings().removeClass('active');
    readmoo.localStorage.setItem('synthetic', 'double');
    readmoo.localStorage.setItem('scrollMode', 'scroll-continuous');
    readerDOM.style.maxWidth = 'none';

    if(scrollToReflowable === false){
        scrollToReflowable = true;
        readerSettings = { isScrollContinuous: true, doNotUpdateView: false};
        updateReader(readerSettings);
        scrollControll();
    } 

    return;
});

