findCurrentSpineItemBookmarkPages = function (collection) {
    if (DEBUG)
        console.log('findCurrentSpineItemBookmarkPages');
    var spineItem, spineItemIndex, page, locators;
    spineItem = reader.getCurrentSpineItem();
    if (spineItem !== null)
        spineItemIndex = reader.getCurrentSpineItem().index;
    else 
        return;

    collection.each(function (model) {
        if (APPLICATION !== 'kindle'){//kindle
            locators = model.get('locators');
        }else{
            try{
                locators = model.attributes.bookmark.locators;
                // console.log("locators1===",locators);
            }catch(e){
                locators = model.get("locators");
                // console.log("locators2===",locators);
            }
        }
        
        if (locators.spineItemIndex !== spineItemIndex) {
            return;
        }

        if (model.hasOwnProperty('_page')) {
            return;
        }

        if (!locators.location) {
            return;
        }

        // page = getPageForElementCfi(locators.location);
        if (locators.location.indexOf('@') == -1)
            page = getPageForTextTerminusOffsetCfi(locators.location);
        else
            page = getPageForElementCfi(locators.location); //TODO page number is not exactly correct.  

        if (_.isNumber(page)) {
            model._page = page;
        }
    });
};

