bookmarkCurrentPage = function () {
    reader.bookmarkCurrentPage(function (bookmark) {
        if (bookmark) {
            // console.log('got bookmark data');
            // console.log(bookmark);
            /*if (_.isNumber(bookmark.pagePercent) && _.isNumber(bookmark.index)) {
                bookmark.percent = epubReadingSystem.pagePercentToPercent(
                    bookmark.index,
                    bookmark.pagePercent);
            }*/

            var foo = reader.getCurrentSpineItemPageInfo(),
                len = foo.openPages.length,
                pageTotal = foo.openPages[len-1].spineItemPageCount,
                pageIndex = foo.openPages[len-1].spineItemPageIndex,
                spineItemIndex = foo.openPages[len-1].spineItemIndex,
                weights = epubReadingSystem.weights,
                weight = spineItemIndex == 0 ? weights[0] : weights[spineItemIndex] - weights[spineItemIndex - 1],
                p = weight - Math.round(((pageIndex + 1) / pageTotal) * weight),
                percent = weights[spineItemIndex] - p;

            bookmark.device = epubReadingSystem.device;

            var data = {
                'bookmark[content]': bookmark.excerpt || '',
                'bookmark[bookmarked_at]': (new Date(bookmark.time)).toISOString(),
                'bookmark[device]': bookmark.device || 'desktop',
                'bookmark[locators]': {
                    'cfi': bookmark.charOffsetCfi,
                    'position': (percent / 100),
                    'title': bookmark.title
                },
                'spineItemIndex': bookmark.index,
                'pagePercent': bookmark.pagePercent
            };

            data.content = data['bookmark[content]'];
            data.bookmarked_at = data['bookmark[bookmarked_at]'];
            data.device = data['bookmark[device]'];
            data.position = (bookmark.pagePercent / 100);

            if (APPLICATION === 'kindle'){//kindle
                data.location = data['bookmark[locators]'].cfi;
            }

            /*data.locators = {
                location: data['bookmark[locators]'].cfi,
                page: data['bookmark[locators]'].contentcfi,
                title: data['bookmark[locators]'].title,
                spineItemIndex: bookmark.index
            };*/

            if (bookmarkCollection.__offline__ && APPLICATION !== 'kindle') {
                readingIdDefer.done((function () {
                    var _data = data;

                    return function (readingId) {
                        _data.readingId = readingId;
                        bookmarkCollection.add(_data).always(function (model) {
                            currentBookmark = model;
                            // console.log('locators: ' + JSON.stringify(model.get('locators')));
                            // console.log(model.get('locators').location.indexOf("@"));
                            if (model.get('locators').location.indexOf("@") == -1 && reader.getCurrentViewType() !== 2)
                                currentBookmark._page = getPageForTextTerminusOffsetCfi(model.get('locators').location);
                            // 2016/06/15 捲動模式也不需使用 getPageForElementCfi
                            else if ((reader.getCurrentViewType() !== 2) && (reader.getCurrentViewType() !== 4))
                                currentBookmark._page = getPageForElementCfi(model.get('locators').location);
                            else
                                currentBookmark._page = model.get('locators').spineItemIndex;
                        });
                    };
                })());
            } else {
                currentBookmark = bookmarkCollection.add(data);
                currentBookmark._page = getPageForElementCfi(
                currentBookmark.get('location'));
            }
        }
    });
};
