hasBookmark = function () {
    return !!getBookmark.apply(null, arguments);
};

getBookmark = function (spineItemIndex, pageIndex) {

    return bookmarkCollection.find(function (model) {
        var locators = APPLICATION === 'kindle' ?
            model.attributes.bookmark.locators :
            model.get('locators');

        var screenHeight = $(document).height();

        if (DEBUG) {
            console.log('locators: ' + JSON.stringify(locators));
            console.log('spineItemIndex: ' + locators.hasOwnProperty('spineItemIndex'));
            console.log('spineItemIndex: ' + spineItemIndex);
            console.log('pageIndex: ' + pageIndex);
            console.log('model._page: ' + model._page);
        }
        
        if (typeof(locators)!== 'undefined') {

            if (reader.getCurrentViewType() == 1) {
                // var spineItem = reader.getSpineItemByIndex(locators.spineItemIndex);
                // console.log('todo getBookmark element in reflowable!');
                // var bar = reader.getElementByCfi(spineItem, locators.location);
                // console.log('get element by cfi: ' + bar);
                // console.log(bar);
                return locators.hasOwnProperty('spineItemIndex') &&
                    locators.spineItemIndex === spineItemIndex &&
                    model.hasOwnProperty('_page') &&
                    model._page === pageIndex;
            } 
            else if (reader.getCurrentViewType() == 2) {
                return locators.hasOwnProperty('spineItemIndex') &&
                    locators.spineItemIndex === spineItemIndex;
            } 
            else { //scrolling mode
                 if (pageIndex !== null) {
                    var contentView = $('#scrolled-content-frame');
                    var views = reader.getVisiblePageViews();
                    if (DEBUG)
                        console.log('Length of visible view: '+views.length);
                    
                    if (views.length > 2)
                        return false;

                    var spineItem = reader.getSpineItemByIndex(spineItemIndex);

                    if (DEBUG)
                        console.log('todo getBookmark in scrolling mode: ' + spineItem);

                    var element = reader.getElementByCfi(spineItem, locators.location);
                    var position = contentView.scrollTop();

                    if (DEBUG)
                        console.log('get element by cfi: ' + element);

                    if (typeof(element) !== 'undefined' && locators.mid.length > 0) {
                        if (typeof(element.getBoundingClientRect) !== 'undefined') {
                             var AnchorRect = element.getBoundingClientRect();
                        } else {
                            if (DEBUG) {
                                console.log(element[0].nodeType);
                                console.log(element);  
                            }
                            var SpanElement = document.createElement('span');
                            SpanElement.innerHTML = "群" ; // 不要用括弧（「」、[]）等符號，因為會造成避頭點的位移現象
                            element[0].parentNode.insertBefore(SpanElement, element[0]) ;
                            var AnchorRect = SpanElement.getBoundingClientRect();
                            element[0].parentNode.removeChild(SpanElement);
                            element[0].parentNode.normalize();
                        }
                    } else if (typeof(element) !== 'undefined') {
                        var AnchorRect = element.getBoundingClientRect();
                    } else {
                        return false;
                    }

                    if ((AnchorRect.top - screenHeight) < position && position < (AnchorRect.top + screenHeight)) {
                        
                        if (DEBUG) {
                            console.log(locators.mid.length);
                            console.log(element);
                            console.log(element.nodeType);
                            console.log('AnchorRect: ');
                            console.log(AnchorRect);
                            console.log('scrollTop: '+contentView.scrollTop());
                            console.log('AnchorRect.top: '+AnchorRect.top);
                        }   
                        return model;
                    }
                    
                }
            }       
        }
    });
};

initialize = function () {

    var itemClickable = true,
        onSpineItemChanged;

    tplBookmarkList = leftPanel.appendToDiv2(tplBookmarkList);

    // Assign page to each bookmark model and 
    // check current page is bookmarked or not.
    onSpineItemChanged = function () {
        findCurrentSpineItemBookmarkPages(bookmarkCollection);
        _.delay(function () {
            var spineItem = reader.getCurrentSpineItem();
            if (hasBookmark(
                    spineItem !== null ? spineItem.index: null,
                    reader.getCurrentPageNumber())
            ) {
                brightBookmark();
            } else {
                dimBookmark();
            }
        }, 300);
    };

    epubReadingSystem.readerReady(function () {
        onSpineItemChanged();
        reader.on([reader.EVENT.RENDER_COMPLETED, reader.EVENT.PAGINATION_CHANGED].join(' '), onSpineItemChanged);
        reader.on(reader.EVENT.VIEW_PAGINATION_CHANGED,
            _.throttle(function (pageInfo) {
                var i = 0,
                    bookmark,
                    len = pageInfo.openPages.length,
                    pageIndex, spineItemIndex;

                for (; i < len ; i++) {
                    pageIndex = pageInfo.openPages[i].spineItemPageIndex;
                    spineItemIndex = pageInfo.openPages[i].spineItemIndex;
                    if (!!(bookmark = getBookmark(spineItemIndex, pageIndex))) {
                        currentBookmark = bookmark;
                        brightBookmark();
                        break;
                    }
                }

                if (!bookmark) {
                    dimBookmark();
                }

                return;

            }, 100, {leading: false, trailing: true})
        );
    });

    bookmarkCollection.comparator = function(model) {
          var dateString = model.get('bookmarked_at');
          var d = new Date(dateString);
          return - d.getTime();
    };

    bookmarkCollection.sort();
    if (APPLICATION !== 'kindle'){//kindle
        insertToPanelList(bookmarkCollection.models);
    }

    if (Modernizr.touch) {
        tplBookmarkList.addEventListener('touchmove', function () {
            itemClickable = false;
        });
    }
    document.querySelector('#left-panel').addEventListener('scroll', function(){
        itemClickable = false;
    });

    tplBookmarkList.addEventListener(
            Modernizr.touch ? 'touchend' : 'click',
            function (event) {
                event.stopPropagation();
                event.preventDefault();
                var target = event.target;
                if (itemClickable) {
                    if (event.target.className === 'del') {
                        if (APPLICATION === 'kindle'){//kindle
                            dimBookmark();
                        }
                        doItemRemove(target);
                    }
                    else {
                        // Do click
                        if (target.nodeName.toUpperCase() !== 'LI' &&
                            target.parentNode.nodeName.toUpperCase() === 'LI') {
                            target = target.parentNode;
                        }

                        doItemClick(target);
                    }
                }
                else {
                    itemClickable = true;
                }
            }
    );
    //按下
    panelView.$el.on('keydown', function(event){
        if (event.keyCode === 40 || event.keyCode === 83){
            $tplBtn.trigger(clickEvent);
        }
    });

    if (reader.getCurrentViewType() == 2) {
        epubReadingSystem.$tplBtn = $tplBtn;
    }

    $tplBtn.on(clickEvent, function(event) {
        event.preventDefault();
        if ($tplBtn.hasClass('active')) {
            dimBookmark();
            removeBookmark(currentBookmark);
        }
        else {
            // 將本頁加入書籤
            brightBookmark();
            bookmarkCurrentPage();
            if (APPLICATION === 'kindle'){//kindle
                addBookmark(currentBookmark);
            }
        }

        panelView.focus();
    });

};

oAuthCheck = function () {

    var oAuthAPI = epubReadingSystem.getPlugin('oauth-api');

    if (oAuthAPI.api) {
        oAuthAPI.getReadingIdAsync(function (readingId) {
            if (readingId) {
                readingIdDefer.resolve(readingId);
            } else {
                if (navigator.onLine && APPLICATION != 'nwapp')
                    readingIdDefer.reject();
            }
        });

        if (!navigator.onLine && APPLICATION == 'nwapp') {
            readingId = readmoo.localStorage.getItem('r' + epubReadingSystem.bookId);
            if (!readingId) {
              global.window.App.bookmarksCollectionDB.find({
                bookid: epubReadingSystem.bookId
              }, function(err, doc) {
                // console.log(doc[0]['readingId']);
                readingId = doc[0]['readingId'];
                readingIdDefer.resolve(readingId);
              });
            } else {
              readingIdDefer.resolve(readingId);
            }
        }
      
    } else {
        var readingId = oAuthAPI.getReadingId();
        if (readingId) {
            readingIdDefer.resolve(readingId);
        } else {
            readingIdDefer.reject();
        }
    }
};

