define('plugin_oauth-api', ['require','exports','module','jquery','underscore','backbone','URIjs/URI'],function (require, exports, module) {
  var $ = require('jquery'),
      _ = require('underscore'),
      Backbone = require('backbone'),
      URI = require('URIjs/URI');
  

  module.exports._dependencies_ = [];
  module.exports._init_ = function () {

    var reader = arguments[0],
        epubReadingSystem = arguments[1];

    var APPLICATION, BOOK_ID, CLIENT_ID, DEBUG, ORIGINAL_HREF, READING_ID_KEY_NAME, REDIRECT_URL, USER_ID_KEY_NAME, api, apiError, createReading, formal, getMe, getReading, localstorage, oAuth, preview, _getReadingDefer, _getReadingIdDefer, _getUserDefer, _reading, _readingId, _user, _userId;

localstorage = readmoo.localStorage || window.localStorage;

DEBUG = false;

ORIGINAL_HREF = '_raoh_';

CLIENT_ID = '8bb43bdd60795d800b16eec7b73abb80';

REDIRECT_URL = 'app://readmoo/';

BOOK_ID = epubReadingSystem.bookId;

APPLICATION = 'nwapp';

USER_ID_KEY_NAME = 'u';

READING_ID_KEY_NAME = "r" + BOOK_ID;

if (DEBUG) {
  console.log("toc=====", CLIENT_ID, REDIRECT_URL, BOOK_ID, APPLICATION, READING_ID_KEY_NAME);
}

if (!(window.readmoo && window.readmoo.OAuthAPI)) {
  return {
    getUserId: function() {
      return localstorage.getItem(USER_ID_KEY_NAME);
    },
    getReadingId: function() {
      return localstorage.getItem(READING_ID_KEY_NAME);
    }
  };
}

_user = void 0;

_userId = void 0;

_reading = void 0;

_readingId = void 0;

_getUserDefer = $.Deferred();

_getReadingDefer = $.Deferred();

_getReadingIdDefer = $.Deferred();

if (!(CLIENT_ID && REDIRECT_URL)) {
  return null;
}

oAuth = new window.readmoo.OAuthAPI(CLIENT_ID, REDIRECT_URL);

api = oAuth.api;

oAuth.config.setDisplay('page');

/*
# @method apiError
*/


apiError = function(name, msg) {
  console.error(("Call api `" + name + "` error occur") + (msg ? " - " + msg : ""));
};

/*
# Create reading
#
# @method createReading
*/


createReading = function() {
  var dtd, options;
  dtd = $.Deferred();
  options = {
    book_id: BOOK_ID
  };
  options['reading[state]'] = api.readings.STATE_READING;
  api.readings(options).createReadingByBookId().success(function(data) {
    var reading;
    if (data.status === 201) {
      reading = data.reading;
      dtd.resolve(reading);
    } else {
      apiError('createReadingByBookId', "status " + data.status);
      dtd.reject();
    }
  }).error(function() {
    apiError('createReadingByBookId');
    dtd.reject();
  });
  return dtd.promise();
};

/*
# Get current user(me) profile
#
# @method getMe
*/


getMe = function() {
  var dtd;
  dtd = $.Deferred();
  api.me().get().success(function(data) {
    var userId;
    userId = data.user.id;
    localstorage.setItem(USER_ID_KEY_NAME, userId);
    dtd.resolve(data.user);
  }).error(function() {
    dtd.reject();
    apiError('me');
  });
  return dtd.promise();
};

/*
# Get reading
#
# @method getReading
*/


getReading = function() {
  var dtd, run;
  dtd = $.Deferred();
  run = function(userId) {
    return api.readings({
      userId: userId,
      book_id: BOOK_ID
    }).getReadingsByUserIdWithMatch().success(function(data) {
      var reading;
      if (data.status === 200 && data.items && data.items.length) {
        reading = data.items[0].reading;
        return dtd.resolve(reading);
      } else {
        return createReading().done(function(reading) {
          dtd.resolve(reading);
        }).fail(function() {
          dtd.reject();
        });
      }
    }).error(function() {
      dtd.reject();
      apiError('getReadingsByUserId');
    });
  };
  if (!_userId) {
    _getUserDefer.promise().done(function(user) {
      run(user.id);
    }).fail(function() {
      dtd.reject();
    });
  } else {
    run(_userId);
  }
  return dtd.promise();
};

formal = function() {
  var href;
  if (!oAuth.online() && APPLICATION !== 'nwapp') {
    localStorage.setItem(ORIGINAL_HREF, location.href);
    oAuth.login();
  } else {
    href = localStorage.getItem(ORIGINAL_HREF);
    localStorage.setItem('_online_', true);
    if (href) {
      localStorage.removeItem(ORIGINAL_HREF);
      location.href = href;
    } else {
      _userId = localstorage.getItem(USER_ID_KEY_NAME);
      getMe().done(function(user) {
        _user = user;
        _userId = user.id;
        _getUserDefer.resolve(user);
      }).fail(function() {
        _getUserDefer.reject();
      });
      getReading().done(function(reading) {
        localstorage.setItem(READING_ID_KEY_NAME, reading.id);
        _reading = reading;
        _readingId = reading.id;
        _getReadingDefer.resolve(reading);
        _getReadingIdDefer.resolve(reading.id);
      }).fail(function() {
        _getReadingDefer.reject();
        _readingId = localstorage.getItem(READING_ID_KEY_NAME);
        if (_readingId) {
          _getReadingIdDefer.resolve(_readingId);
        } else {
          _getReadingIdDefer.reject();
        }
      });
    }
  }
};

preview = function() {
  _getUserDefer.reject();
  _getReadingDefer.reject();
  _getReadingIdDefer.reject();
  return localStorage.setItem('__oa__', JSON.stringify({
    sync_service: "readmoo",
    readmoo: {
      access_token: "",
      state: "",
      client_id: CLIENT_ID,
      network: "readmoo",
      display: "page"
    }
  }));
};

if (epubReadingSystem.isPreview) {
  preview();
} else {
  formal();
}

return _.extend({}, oAuth, {
  getUser: function() {
    return _user;
  },
  getUserAsync: function(callback) {
    _getUserDefer.promise().always(function(user) {
      callback(user);
    });
  },
  getUserId: function() {
    return _userId;
  },
  getUserIdAsync: function(callback) {
    if (_userId) {
      callback(_userId);
    } else {
      _getUserDefer.promise().always(function(user) {
        callback(user ? user.id : null);
      });
    }
  },
  getReading: function() {
    return _reading;
  },
  getReadingAsync: function(callback) {
    if (!navigator.onLine && APPLICATION === 'nwapp') {
      global.window.App.readingsCollectionDB.find({
        bookid: BOOK_ID
      }, function(err, docs) {
        return callback(docs[0]['reading']);
      });
      return;
    }
    _getReadingDefer.always(function(reading) {
      if (navigator.onLine && APPLICATION === 'nwapp') {
        global.window.App.readingsCollectionDB.findOne({
          bookid: BOOK_ID
        }, function(err, doc) {
          if (doc === null) {
            global.window.App.readingsCollectionDB.insert([
              {
                bookid: BOOK_ID,
                reading: reading
              }
            ]);
          } else {
            global.window.App.readingsCollectionDB.update({
              bookid: BOOK_ID
            }, {
              $set: {
                reading: reading
              }
            }, {}, function(err, numReplaced) {
              console.log('update success!' + numReplaced);
            });
          }
        });
      }
      callback(reading);
    });
  },
  getReadingId: function() {
    return _readingId;
  },
  getReadingIdAsync: function(callback) {
    _getReadingIdDefer.always(function(readingId) {
      callback(readingId);
    });
  }
});

  };
});
define("oauth-api_wrap", function(){});


